// src/Pages/KioskPlanDetail.js
import React, { useEffect, useState } from "react";
import { useLocation, useParams, Link } from "react-router-dom";
import { getPlanById } from "../services/kioskPlanApi";

// Format ngày về dd/MM/yyyy (vi-VN)
// - Hỗ trợ ISO string (YYYY-MM-DD, ISO datetime)
// - Hỗ trợ Excel serial date (ví dụ: 45799)
// - Trả "-" nếu không hợp lệ
const formatDate = (value) => {
  if (value === null || value === undefined || value === "") return "-";

  // Excel serial (number hoặc string toàn số)
  const isNumericString = typeof value === "string" && /^\d+$/.test(value);
  if (typeof value === "number" || isNumericString) {
    const num = Number(value);
    if (!Number.isNaN(num)) {
      // Excel serial tính từ 1899-12-30 (đã bao gồm bug 1900-02-29)
      const baseUTC = Date.UTC(1899, 11, 30);
      const ms = baseUTC + num * 24 * 60 * 60 * 1000;
      const d = new Date(ms);
      if (!Number.isNaN(d.getTime())) {
        return d.toLocaleDateString("vi-VN");
      }
    }
  }

  // Thử parse theo JS Date (ISO hoặc các format JS hiểu được)
  const d2 = new Date(value);
  if (!Number.isNaN(d2.getTime())) {
    return d2.toLocaleDateString("vi-VN");
  }

  // Thử parse thủ công dạng dd/MM/yyyy rồi vẫn format lại cho đồng nhất
  if (typeof value === "string") {
    const m = value.trim().match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/);
    if (m) {
      const [, dd, mm, yyyy] = m;
      const d3 = new Date(Date.UTC(Number(yyyy), Number(mm) - 1, Number(dd)));
      if (!Number.isNaN(d3.getTime())) {
        return d3.toLocaleDateString("vi-VN");
      }
    }
  }

  return "-";
};

const KioskPlanDetail = () => {
  const { id } = useParams();
  const location = useLocation();
  const [plan, setPlan] = useState(location.state?.plan || null);
  const [loading, setLoading] = useState(!location.state?.plan);

  useEffect(() => {
    const fetchOne = async () => {
      try {
        setLoading(true);
        const res = await getPlanById(id);
        setPlan(res.data);
      } finally {
        setLoading(false);
      }
    };
    if (!plan) fetchOne();
  }, [id, plan]);

  if (loading) return <div className="p-4">Đang tải chi tiết...</div>;
  if (!plan) return <div className="p-4">Không tìm thấy dữ liệu.</div>;

  const fields = [
    { label: "STT", value: plan.stt },
    { label: "Tên bệnh viện", value: plan.hospitalName },
    { label: "Ghi chú làm việc gần nhất", value: plan.lastNote },
    { label: "Yêu cầu thêm của bệnh viện", value: plan.additionalRequest },

    // ⬇️ 3 trường ngày được format chuẩn vi-VN
    { label: "Ngày phát sinh yêu cầu", value: formatDate(plan.requestDate) },
    { label: "Deadline", value: formatDate(plan.deadline) },
    { label: "Ngày chuyển nghiệm thu", value: formatDate(plan.deliveryDate) },

    { label: "Số lượng", value: plan.quantity },
    { label: "Loại đầu đọc CCCD", value: plan.cccdReaderType },
    { label: "Loại thiết bị", value: plan.deviceType },
    { label: "Mức độ ưu tiên", value: plan.priorityLevel },
    { label: "Người phụ trách", value: plan.personInCharge },
    { label: "Trạng thái làm việc với viện - dev", value: plan.devStatus },
    { label: "Trạng thái xử lý yêu cầu", value: plan.requestStatus },
    { label: "Người xử lý", value: plan.handler },
    { label: "His", value: plan.his },
    { label: "Url port", value: plan.urlPort },
    { label: "Tài khoản check BHXH", value: plan.bhxhAccount },
    { label: "ID", value: plan._id },
  ];

  return (
    <div className="p-6">
      <div className="mb-4 flex items-center justify-between">
        <h1 className="text-lg font-semibold">Chi tiết kế hoạch kiosk</h1>
        <Link
          to="/kiosk-plans"
          className="px-3 py-1 border rounded hover:bg-gray-50 text-sm"
        >
          Quay lại danh sách
        </Link>
      </div>

      <div className="grid md:grid-cols-2 gap-4">
        {fields.map((f) => (
          <div key={f.label} className="border rounded p-3">
            <div className="text-xs text-gray-500">{f.label}</div>
            <div className="text-sm">{String(f.value ?? "-")}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default KioskPlanDetail;
