import React, { useState } from "react";
import { X } from "lucide-react";

const defaultOptions = {
  priorityLevel: [
    "Bình thường (1 tuần - 3 tuần)",
    "Gấp (2 - 3 ngày)",
    "Hỏa tốc (Trong ngày)",
  ],
  devStatus: ["Chờ dev build update", "Test thông api & chuyển dev"],
  hopStatus: ["Chưa tích hợp", "Đã tích hợp", "Nghiệm thu", "Hủy yêu cầu"],
  personInCharge: ["Minh", "Kiệt", "Giang", "Thành", "Toàn", "Hải", "Dương"],
};

const EditDropdownModal = ({ onClose, onSave, initialOptions }) => {
  const [options, setOptions] = useState(initialOptions || defaultOptions);

  const handleChange = (key, index, value) => {
    const newList = [...options[key]];
    newList[index] = value;
    setOptions((prev) => ({ ...prev, [key]: newList }));
  };

  const handleAdd = (key) => {
    setOptions((prev) => ({
      ...prev,
      [key]: [...prev[key], ""],
    }));
  };

  const handleRemove = (key, index) => {
    const newList = [...options[key]];
    newList.splice(index, 1);
    setOptions((prev) => ({ ...prev, [key]: newList }));
  };

  const handleSubmit = () => {
    onSave(options);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Chỉnh sửa dropdown</h2>
          <button onClick={onClose}>
            <X />
          </button>
        </div>

        {Object.entries(options).map(([key, list]) => (
          <div key={key} className="mb-4">
            <div className="text-sm font-medium mb-1">{key}</div>
            {list.map((item, idx) => (
              <div key={idx} className="flex gap-2 mb-1">
                <input
                  value={item}
                  onChange={(e) => handleChange(key, idx, e.target.value)}
                  className="border p-1 rounded w-full text-sm"
                />
                <button
                  onClick={() => handleRemove(key, idx)}
                  className="text-red-500 text-xs"
                >
                  Xoá
                </button>
              </div>
            ))}
            <button
              onClick={() => handleAdd(key)}
              className="text-xs text-blue-600"
            >
              + Thêm giá trị
            </button>
          </div>
        ))}

        <div className="text-right mt-4">
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-4 py-2 rounded text-sm hover:bg-blue-700"
          >
            Lưu thay đổi
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditDropdownModal;
