import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { logo, background } from "../Assets/index";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("");
  const [password, setPassword] = useState("");

  const [message, setMessage] = useState(null); // ‚úÖ Th√™m state message
  const [messageType, setMessageType] = useState("success"); // "success" | "error"

  const navigate = useNavigate();

  const registerUser = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify({
          name,
          email,
          role,
          password,
        }),
      });
      const data = await response.json();

      if (response.ok) {
        setMessage("üéâ ƒêƒÉng k√Ω th√†nh c√¥ng!");
        setMessageType("success");

        setTimeout(() => {
          navigate("/");
        }, 1500);
      } else {
        setMessage(data.message || "‚ùå ƒêƒÉng k√Ω th·∫•t b·∫°i!");
        setMessageType("error");
      }
    } catch (error) {
      setMessage("‚ùå C√≥ l·ªói x·∫£y ra khi k·∫øt n·ªëi server!");
      setMessageType("error");
    }

    // T·ª± ·∫©n th√¥ng b√°o sau 3s
    setTimeout(() => {
      setMessage(null);
    }, 3000);
  };

  return (
    <div
      className="flex flex-col items-center justify-center h-screen"
      style={{
        backgroundImage: `url(${background})`,
        backgroundSize: "cover",
      }}
    >
      <div className="text-center mt-5">
        <img
          className="w-80 h-70 mb-15 mx-auto min-w-[150px]"
          src={logo}
          alt="logo"
        />

        {/* ‚úÖ Th√¥ng b√°o ƒë·ªông */}
        {message && (
          <div
            className={`mb-4 px-4 py-3 rounded-lg text-white font-medium shadow-lg transition-all duration-300 ${
              messageType === "success" ? "bg-green-500" : "bg-red-500"
            }`}
          >
            {message}
          </div>
        )}

        <form onSubmit={registerUser} className="mx-auto mb-5 mt-4 max-w-md">
          <div className="mb-8 text-left">
            <h1 className="text-3xl font-bold mt-4 mb-2 text-gray-600">
              Register
            </h1>
            <p className="text-gray-500">
              Already have an account?{" "}
              <span
                onClick={(e) => navigate("/")}
                className="text-blue-600 cursor-pointer"
              >
                Sign In
              </span>
            </p>
          </div>

          <div className="text-left">
            <span className="">H·ªç t√™n</span>
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Full Name"
              type="text"
              className="w-full px-4 py-4 border rounded-lg mb-4"
            />
          </div>
          <div className="text-left">
            <span className="">Email</span>
            <input
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              type="email"
              className="w-full px-4 py-4 border rounded-lg mb-4"
            />
          </div>
          <div className="text-left">
            <span className="">M·∫≠t kh·∫©u</span>
            <input
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              type="password"
              className="w-full px-4 py-4 border rounded-lg mb-4"
            />
          </div>
          <div className="text-left">
            <span className="">Quy·ªÅn</span>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full px-4 py-4 border rounded-lg mb-4"
            >
              <option value="">-- Ch·ªçn quy·ªÅn --</option>
              <option value="admin">Admin</option>
              <option value="client">Ng∆∞·ªùi d√πng</option>
            </select>
          </div>

          <div className="flex justify-between">
            <button
              type="submit"
              className="bg-blue-600 text-white py-2 px-4 rounded-sm"
            >
              ƒêƒÉng k√Ω
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
