import React from "react";

// Hàm format ngày (Excel serial, ISO, string)
const formatDate = (value) => {
  if (value === null || value === undefined || value === "") return "-";

  // Excel serial number (hoặc string toàn số)
  const isNumericString = typeof value === "string" && /^\d+$/.test(value);
  if (typeof value === "number" || isNumericString) {
    const num = Number(value);
    if (!Number.isNaN(num)) {
      const baseUTC = Date.UTC(1899, 11, 30);
      const ms = baseUTC + num * 24 * 60 * 60 * 1000;
      const d = new Date(ms);
      if (!Number.isNaN(d.getTime())) return d.toLocaleDateString("vi-VN");
    }
  }

  // ISO string hoặc JS hiểu được
  const d2 = new Date(value);
  if (!Number.isNaN(d2.getTime())) {
    return d2.toLocaleDateString("vi-VN");
  }

  // dd/MM/yyyy
  if (typeof value === "string") {
    const m = value.trim().match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/);
    if (m) {
      const [, dd, mm, yyyy] = m;
      const d3 = new Date(Date.UTC(Number(yyyy), Number(mm) - 1, Number(dd)));
      if (!Number.isNaN(d3.getTime())) {
        return d3.toLocaleDateString("vi-VN");
      }
    }
  }

  return "-";
};

const KioskPlanTable = ({ data, onEdit, onDelete, onView }) => {
  return (
    <table className="min-w-full text-sm text-left border">
      <thead className="bg-gray-100 text-xs font-semibold">
        <tr>
          {[
            "STT",
            "Tên bệnh viện",
            "Deadline",
            "Mức độ ưu tiên",
            "Trạng thái làm việc với viện - dev",
            "Trạng thái xử lý yêu cầu",
            "Ngày chuyển nghiệm thu",
            "Hành động",
          ].map((header, i) => (
            <th key={i} className="p-2 border">{header}</th>
          ))}
        </tr>
      </thead>

      <tbody>
        {data.map((plan, idx) => (
          <tr key={plan._id || idx} className="border-t hover:bg-gray-50">
            <td className="p-2 border">{plan.stt || idx + 1}</td>
            <td className="p-2 border">{plan.hospitalName}</td>
            <td className="p-2 border">{formatDate(plan.deadline)}</td>
            <td className="p-2 border">{plan.priorityLevel || "-"}</td>
            <td className="p-2 border">{plan.devStatus || "-"}</td>
            <td className="p-2 border">{plan.requestStatus || "-"}</td>
            <td className="p-2 border">{formatDate(plan.deliveryDate)}</td>

            {/* Ô Hành động */}
            <td className="p-2 border">
              <div className="flex items-center gap-2 whitespace-nowrap min-w-[220px]">
                <button
                  type="button"
                  className="inline-flex items-center px-2 py-1 rounded text-xs bg-blue-500 text-white hover:bg-blue-600 active:scale-[.98] transition"
                  onClick={() => onEdit(plan)}
                >
                  Sửa
                </button>
                <button
                  type="button"
                  className="inline-flex items-center px-2 py-1 rounded text-xs bg-red-500 text-white hover:bg-red-600 active:scale-[.98] transition"
                  onClick={() => onDelete(plan._id)}
                >
                  Xoá
                </button>
                <button
                  type="button"
                  className="inline-flex items-center px-2 py-1 rounded text-xs bg-gray-700 text-white hover:bg-gray-800 active:scale-[.98] transition"
                  onClick={() => onView(plan)}
                >
                  Chi tiết
                </button>
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default KioskPlanTable;
