import React, { useState } from "react";
import Menubar from "./Components/Menubar";
import Navbar from "./Components/Navbar";
import MenuToggle from "./Components/MenuToggle";
import { Outlet, useLocation } from "react-router-dom";

const Layout = () => {
  const [showMenu, setShowMenu] = useState(false);
  const location = useLocation();
  const page = location.pathname.split("/")[1] || "Dashboard";

  const handleMenuToggle = () => {
    setShowMenu((prev) => !prev);
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-screen z-20 bg-gray-100 transition-all duration-300 ease-in-out 
        ${showMenu ? "w-[250px]" : "w-0"} 
        lg:w-[250px] overflow-hidden`}
      >
        <Menubar />
      </div>

      {/* Main layout */}
      <div
        className={`flex-1 flex flex-col transition-all duration-300 ease-in-out 
        ${showMenu ? "ml-[250px]" : "ml-0"} 
        lg:ml-[250px]`}
      >
        {/* Navbar */}
        <div className="fixed top-0 left-0 right-0 z-10 lg:left-[250px] bg-white shadow-md h-16 flex items-center justify-between px-4">
          <MenuToggle showMenu={showMenu} handleMenuToggle={handleMenuToggle} />
          <Navbar pagename={page} />
        </div>

        {/* Main content */}
        <div className="mt-16 p-4 overflow-y-auto h-full bg-gray-50">
          <Outlet />
        </div>
      </div>

      {/* Overlay for mobile */}
      {showMenu && (
        <div
          className="fixed inset-0 bg-black opacity-30 z-10 lg:hidden"
          onClick={handleMenuToggle}
        ></div>
      )}
    </div>
  );
};

export default Layout;
