import React, { useState, useEffect } from "react";
import {
  logo,
  dashboard,
  home,
  user,
  showcase,
  settings,
  role,
  market,
  content,
  customer,
} from "../Assets/index";
import { getUser } from "../utils/auth";
import { useLocation } from "react-router-dom";

const Menubar = () => {
  const [userRole, setUserRole] = useState(null);
  const location = useLocation(); // 👈 Để biết đường dẫn hiện tại

  useEffect(() => {
    const user = getUser();
    setUserRole(user?.role);
  }, []);

  const menuItems = [
    { name: "Dashboard", icon: dashboard, link: "/dashboard" },
    { name: "User Management", icon: user, link: "/user-management" },
    { name: "Customer Management", icon: customer, link: "/customer-management" },
    { name: "Content Management", icon: content, link: "/content-management" },
    { name: "Showcase Management", icon: showcase, link: "/showcase-management" },
    { name: "Home Service Management", icon: home, link: "/home-service" },
    { name: "Market Place Management", icon: market, link: "/market-place" },
    ...(userRole === "admin"
      ? [{ name: "Permission & Role", icon: role, link: "/role-management" }]
      : []),
    { name: "Settings", icon: settings, link: "/settings" },
  ];

  return (
    <div className="flex flex-col h-full w-full bg-gray-100">
      <div className="flex items-center justify-center h-20">
        <img src={logo} alt="Company Logo" className="h-20 w-30 pt-5" />
      </div>

      <div className="flex-1 flex flex-col justify-start">
        <div className="flex flex-col items-start pt-20">
          <span className="px-5 py-2 text-black font-semibold">Menu</span>
          {menuItems.map((item, index) => {
            const isActive = location.pathname === item.link;
            return (
              <a
                key={index}
                href={item.link}
                className={`px-4 py-3 flex items-center w-full rounded-lg transition-all duration-200
                  ${isActive
                    ? "bg-[#4f8aff] text-white font-semibold"
                    : "hover:bg-[#e6f0ff] hover:text-[#4f8aff] text-gray-700"
                  }`}
              >
                <img src={item.icon} alt={item.name} className={`h-5 w-5 mr-2 ${isActive ? "filter brightness-200" : ""}`} />
                <span className="hidden sm:inline-block">{item.name}</span>
              </a>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Menubar;
